    _titleIsNull: function(){
        var titleText = this._getTitleText();
        var text = Z.Util.stringTrim();

        if(titleText){
            if(typeof titleText === "string"){
                var text = Z.Util.stringTrim(titleText);

                if(text && text.length > 0){
                    return false;
                }else{
                    return true;
                }
            }else{
                return false;
            }
        }else{
            return true;
        }


    }
